changeSet.sql:
CREATE TABLE USERS ( ID NUMBER(19) NOT NULL, NAME VARCHAR2(255) NOT NULL, EMAIL VARCHAR2(255) UNIQUE, PRIMARY KEY (ID) );
CREATE TABLE CLIENTS ( ID NUMBER(19) NOT NULL, TYPES VARCHAR2(255) NOT NULL, FULL_NAME VARCHAR2(255) NOT NULL, ADDRESS VARCHAR2(255) NOT NULL, PHONE VARCHAR2(12) UNIQUE, EMAIL VARCHAR2(255) UNIQUE, BIRTHDAY DATE, PRIMARY KEY (ID) ); CREATE INDEX IDX_CLIENT_FULL_NAME ON CLIENTS(FULL_NAME) PARALLEL 8;
CREATE TABLE ACCOUNTS ( ACCOUNT_NUMBER NUMBER(19) NOT NULL, OWNER NUMBER(19) NOT NULL, ACCOUNT NUMBER(19) NOT NULL, DATE_OPEN DATE NOT NULL, VALIDITY_PERIOD DATE, AMOUNT NUMBER(38) NOT NULL, FOREIGN KEY (OWNER) REFERENCES CLIENTS(ID), PRIMARY KEY (ACCOUNT_NUMBER) ); CREATE INDEX IDX_ACCOUNT_ACCOUNT ON ACCOUNTS (ACCOUNT) PARALLEL 8;
CREATE TABLE TRANSACTIONS ( ID NUMBER(19) NOT NULL, ACCOUNT_FROM_ID NUMBER(19) NOT NULL, ACCOUNT_TO_ID NUMBER(19) NOT NULL, AMOUNT NUMBER(38) NOT NULL, DATA_TIME DATE NOT NULL, TRANSACTION_TYPE VARCHAR2(255) NOT NULL, PRIMARY KEY (ID) );

changeSet.rollback:
DROP TABLE TRANSACTIONS;
DROP TABLE ACCOUNTS;
DROP TABLE CLIENTS;
